import { Member, Collection } from './interface.generated'

export type AuthUser = Member
export type SpaceCollection = Collection

export enum ApiErrorCodes {
  UNKNOWN_ERROR = 1,
  SERVER_ERROR = 10,
  PARAMETER_VALIDATION_FAILED = 100,
  PERMISSION_ERROR = 102,
  USER_REQUEST_REACHED = 104,
  INVALID_PARAMETER = 106,
  INVALID_ACCESS_TOKEN = 108,
  RESOURCE_NOT_FOUND = 110,
  MISSING_PARAMETER = 112,
  PARAMETER_TYPE_PROBLEM = 114,
  DATABASE_ERROR = 116,
  INVALID_REQUEST = 118,
  REQUEST_BLOCKED_FOR_POLICIES = 120,
  BACKEND_ERROR = 122,
}

export interface ApiErrorResponse {
  code: ApiErrorCodes
  message: string
  title?: string
  help?: string
  timestamp: string
  errors?: ApiSubError[]
}
export interface ApiSubError {
  field?: string
  subcode: ApiErrorCodes
  code?: ApiErrorCodes
  message: string
}
